import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import contractAbi from './Tokens/abi'; //nft
import React, { Component } from 'react';
import ConnectBtn from './Components/ConnectBtn';
import LockToken from './Components/LockToken';
import UnlockToken from './Components/UnlockToken';
import Web3Modal from "web3modal";

//import ConnectButton from "./Components/ConnectButton";


class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      isConnected: false,
      isLocked: false,
      contractInstance: null,
      account: null,
      isLoading: false
    };

    const providerOptions = {
    };

    this.web3Modal = new Web3Modal({
      network: "mainnet", // optional
      cacheProvider: true, // optional
      providerOptions // required
    });

    this.connectMetamask = this.connectMetamask.bind(this);
    this.lockToken = this.lockToken.bind(this);
    this.unlockToken = this.unlockToken.bind(this);
    this.isTokenLocked = this.isTokenLocked.bind(this);

  }

  componentDidMount(){
    if (this.web3Modal.cachedProvider) {
      this.onConnect();
    }
  }

  componentDidUpdate(){

  }

  async onConnect () {
    // get provider
    this.web3Modal.connect().then((res) => {
      if (window.ethereum){
        this.provider = window.ethereum;
      } else if (window.web3){
        this.provider = window.web3.currentProvider
      } else {
        this.provider = new Web3.providers.HttpProvider('http://localhost:8545');
      }

      // creste web3 with current provider
      this.web3 = new Web3(this.provider);

      // retreive account info
      this.web3.eth.getAccounts().then((res) => {

        // create contract instance
        this.contractAddress = '0xa4a8d3E5A436EE537c83f83c9b70E91242e7a8D7';
        const contract = new this.web3.eth.Contract(contractAbi, this.contractAddress);

        this.setState({
          account: res[0],
          contractInstance: contract,
          isConnected: true
        });

      }).catch((err) => {
        console.log("err getAccount");  
      });
    }).catch((err) => {
      console.log("err web3Modal.connect");
    });
}

  isTokenLocked() {
    this.state.contractInstance.methods.isLocked().call({
      from: this.state.account,
    }).then((receipt) => {
      console.log(receipt);
    }).catch((err) => {
      console.log(err);
    });
  }

  lockToken() {
    this.state.contractInstance.methods.lock().send({
      from: this.state.account,
    }).then((receipt) => {
      console.log(receipt);
      this.setState({isLocked: true});
    }).catch((err) => {
      console.log(err);
    });
  }

  unlockToken() {
    this.state.contractInstance.methods.unlock().send({
      from: this.state.account,
    }).then((receipt) => {
      this.setState({isLocked: false});
    }).catch((err) => {
      console.log(err);
    });
  }

  connectMetamask() {
    this.onConnect();
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <div> Text: {this.state.isConnected}</div>
          {!this.state.isConnected ? <ConnectBtn 
            connectMetamask={this.connectMetamask}/>
          : <div>
              Logged in <br>{this.state.account}</br>
            </div>
          }

          {
            !this.state.isLocked 
            ?  <LockToken 
                lockToken={this.lockToken}/>
            : <UnlockToken 
                unlockToken={this.unlockToken}/> 
          }
            <br></br>
            <button onClick={() => this.isTokenLocked() } className="button is-outlined is-small is-danger">
              Check Token
            </button>
        </header>
      </div>
    );
  }
}

export default App;
