import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import contractAbi from './Tokens/abi'; //nft
import React, { Component } from 'react';
import ConnectBtn from './Components/ConnectBtn';
import LockToken from './Components/LockToken';
import UnlockToken from './Components/UnlockToken';
import Web3Modal from "web3modal";
//import ConnectButton from "./Components/ConnectButton";


class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      isConnected: false,
      isLocked: false
    };

    const providerOptions = {
      /* See Provider Options Section */
    };

    this.web3Modal = new Web3Modal({
      network: "mainnet", // optional
      cacheProvider: true, // optional
      providerOptions // required
    });

    this.connectMetamask = this.connectMetamask.bind(this);
    this.lockToken = this.lockToken.bind(this);
    this.unlockToken = this.unlockToken.bind(this);

    // this.disconnectMetamask = this.disconnectMetamask.bind(this);
    // this.setupWeb3UI = this.setupWeb3UI.bind(this);


    // this.setupWeb3UI();

  }

  static getDerivedStateFromProps(props, state) {
    // TODO
    // get connected? if connected render button as username else connect button
    // get isLocked if locked render as locked ui else render non-lock
    return {favoritecolor: props.favcol };
  }

  async onConnect () {
    return await this.web3Modal.connect();
  }

  async getAccount() {
    const accounts = await this.web3.eth.getAccounts();
    return accounts[0];
  }

  isTokenLocked() {
    this.contractInstance.methods.isLocked().send({
      from: this.account,
    }).then((receipt) => {
      console.log(receipt);
    }).catch((err) => {
      console.log(err);
    });
  }

  lockToken() {
    this.contractInstance.methods.lock().send({
      from: this.account,
    }).then((receipt) => {
      console.log(receipt);
    }).catch((err) => {
      console.log(err);
    });
  }

  unlockToken() {
    this.contractInstance.methods.unlock().send({
      from: this.account,
    }).then((receipt) => {
      console.log(receipt);
    }).catch((err) => {
      console.log(err);
    });
  }

  connectMetamask() {
    this.onConnect().then((res) => {
      this.provider = res
      this.web3 = new Web3(this.provider);
      this.contractAddress = '0x984593222E93D8140DD7A515e947E9806d01D085';
      this.contractInstance = new this.web3.eth.Contract(contractAbi, this.contractAddress);
      // retreive account info
      this.getAccount().then((res) => {
        this.account = res;
      }).catch((err) => {
        console.log(err)
      });

    }).catch((err) => {
      console.log(err)
    });
  }

  disconnectMetamask(){
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          {/* <MenuBar 
          connectMetamask={this.connectMetamask} 
          disconnectMetamask={this.disconnectMetamask} 
          isConnected={this.state.isConnected} /> */}
          <ConnectBtn 
            connectMetamask={this.connectMetamask}/>
          <LockToken 
            lockToken={this.lockToken}
            isTokenLocked={this.isTokenLocked}/>
          <UnlockToken 
            unlockToken={this.unlockToken}
            isTokenLocked={this.isTokenLocked}/>
        </header>
      </div>
    );
  }
}

export default App;
