import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import contractAbi from './abi'; //nft
import React, { Component } from 'react';



class App extends Component {
  constructor() {
    super();

  this.contractAddress = '0x743B0f7FccE1c41661A0118F2cF37Cd500FcdC05';

  }

  getWeb3 = () => new Promise((resolve) => {
    window.addEventListener('load', () => {
      let currentWeb3;

      if (window.ethereum) {
        currentWeb3 = new Web3(window.ethereum);
        try {
          // Request account access if needed
          window.ethereum.enable();
          // Acccounts now exposed
          resolve(currentWeb3);
        } catch (error) {
          // User denied account access...
          alert('Please allow access for the app to work');
        }
      } else if (window.web3) {
        window.web3 = new Web3(web3.currentProvider);
        // Acccounts always exposed
        resolve(currentWeb3);
      } else {
        console.log('Non-Ethereum browser detected. You should consider trying MetaMask!');
      }
    });
  });

  connectMetamask(){
    this.getWeb3().then((res) => {
      this.web3 = res;
      this.contractInstance = new this.web3.eth.Contract(contractAbi, this.contractAddress);
      this.web3.eth.getAccounts().then((accounts) => {
        [this.account] = accounts;
      }).catch((err) => {
        console.log(err, 'err!!');
      });
    });
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <button onClick={() => this.connectMetamask() } className="button is-outlined is-small is-danger">
                        Buy
          </button>
        </header>
      </div>
    );
  }
}

export default App;
