import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import contractAbi from './Tokens/abi'; //nft
import React, { Component } from 'react';
import ConnectBtn from './Components/ConnectBtn';
import LockToken from './Components/LockToken';
import UnlockToken from './Components/UnlockToken';
import Web3Modal from "web3modal";
//import ConnectButton from "./Components/ConnectButton";


class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      isLocked: false,
      web3: null,
      provider: null,
      connected: true,
      address: '',
      chainId: 0,
      networkId: 0
    };

    const providerOptions = {
      /* See Provider Options Section */
    };

    this.web3Modal = new Web3Modal({
      network: "mainnet", // optional
      cacheProvider: true, // optional
      providerOptions // required
    });

    this.connectMetamask = this.connectMetamask.bind(this);
    this.lockToken = this.lockToken.bind(this);
    this.unlockToken = this.unlockToken.bind(this);
    this.isTokenLocked = this.isTokenLocked.bind(this);
  }

  componentDidMount(){
    if (this.web3Modal.cachedProvider) {
      this.onConnect();
    }
  }

  componentDidUpdate(){

  }

  async onConnect () {

    await this.web3Modal.connect().then((res) =>{
      this.provider = res;
      // subscribeProvider
      this.initWeb3();
    }).catch((err) => {
      // todo
    });

    await this.web3.eth.getAccounts().then((res) => {
      this.address = res[0];
    }).catch((err) => {
      //todo
    });

    const networkId = await this.web3.eth.net.getId();

    const chainId = await this.web3.eth.chainId();

    await this.setState({
      web3,
      provider,
      connected: true,
      address,
      chainId,
      networkId
    });
    await this.getAccountAssets();

  }

  initWeb3(){
    this.web3 = new Web3(this.provider);

    this.web3.eth.extend({
      methods: [
        {
          name: "chainId",
          call: "eth_chainId",
          outputFormatter: web3.utils.hexToNumber
        }
      ]
    });  
  }

  subscribeProvider(){

  }

  connectMetamask() {
    this.onConnect().then((res) => {
      this.provider = res
      this.web3 = new Web3(this.provider);
      this.contractAddress = '0x984593222E93D8140DD7A515e947E9806d01D085';
      this.contractInstance = new this.web3.eth.Contract(contractAbi, this.contractAddress);
      // retreive account info
      this.getAccount().then((res) => {
        this.account = res;
        this.setState({isConnected: true});
      }).catch((err) => {
        console.log(err)
      });

    }).catch((err) => {
      console.log(err)
    });
  }

  // async getAccount() {
  //   const accounts = await this.web3.eth.getAccounts();
  //   return accounts[0];
  // }

  isTokenLocked() {
    this.contractInstance.methods.isLocked().call({
      from: this.account,
    }).then((receipt) => {
      console.log(receipt);
    }).catch((err) => {
      console.log(err);
    });
  }

  lockToken() {
    this.contractInstance.methods.lock().send({
      from: this.account,
    }).then((receipt) => {
      console.log(receipt);
    }).catch((err) => {
      console.log(err);
    });
  }

  unlockToken() {
    this.contractInstance.methods.unlock().send({
      from: this.account,
    }).then((receipt) => {
      console.log(receipt);
      this.setState({isConnected: false});
    }).catch((err) => {
      console.log(err);
    });
  }



  disconnectMetamask(){
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <div> Text: {this.state.isConnected}</div>
          {!this.state.isConnected ? <ConnectBtn 
            connectMetamask={this.connectMetamask}/>
          : <div>Logged in</div>}
          <LockToken 
            lockToken={this.lockToken}
            isTokenLocked={this.isTokenLocked}/>
          <UnlockToken 
            unlockToken={this.unlockToken}
            isTokenLocked={this.isTokenLocked}/>
        </header>
      </div>
    );
  }
}

export default App;
