import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import contractAbi from './Tokens/abi'; //nft
import React, { Component } from 'react';
//import MenuBar from './Components/MenuBar';
import Web3Modal from "web3modal";
//import ConnectButton from "./Components/ConnectButton";


class App extends Component {

  constructor(props) {
    super(props);

    // this.state = {
    //   isConnected: false
    // };

    const providerOptions = {
      /* See Provider Options Section */
    };

    this.web3Modal = new Web3Modal({
      network: "mainnet", // optional
      cacheProvider: true, // optional
      providerOptions // required
    });

    this.provider = null;

    this.onConnect()
    .then((res) => {
      this.provider = res
      this.web3 = new Web3(this.provider);
      this.contractAddress = '0x984593222E93D8140DD7A515e947E9806d01D085';
      this.contractInstance = new this.web3.eth.Contract(contractAbi, this.contractAddress);
      this.getAccount()
    .then((res) => {
      this.account = res;
      this.lockToken();
    }).catch((err) => {
      console.log(err)
    });
    }).catch((err) => {
      console.log(err)
    });



    // this.connectMetamask = this.connectMetamask.bind(this);
    // this.disconnectMetamask = this.disconnectMetamask.bind(this);
    // this.setupWeb3UI = this.setupWeb3UI.bind(this);


    // this.setupWeb3UI();




  }

  async onConnect () {
    return await this.web3Modal.connect();
  }

  async getAccount() {
    const accounts = await this.web3.eth.getAccounts();
    return accounts[0];
  }

  lockToken() {
    this.contractInstance.methods.isLocked().send({
      from: this.account,
    }).then((receipt) => {
      console.log(receipt);
    }).catch((err) => {
      console.log(err);
    });
  }

  connectMetamask() {
    if (window.ethereum) {
      window.ethereum.request({ method: 'eth_requestAccounts' 
      }).then((res) => {
        this.setupWeb3UI();
        console.log("connectedToMetamask");
        this.account = res;
        console.log(this.account);
        this.setupWeb3();
      }).catch((err) => {
        console.log(err)
        console.log("user denied connectedToMetamask")
      });    
    } else {
      // TODO alert
    }
  }

  setupWeb3UI(){
    if(window.ethereum.isConnected() && !this.state.isConnected){ // T T T,T F T, F T F, F F F 
      this.setState({isConnected: !this.state.isConnected});
    } else if(!window.ethereum.isConnected() && this.state.isConnected) {
      this.setState({isConnected: !this.state.isConnected});
    } else if(!window.ethereum.isConnected() && !this.state.isConnected) {
      this.setState({isConnected: this.state.isConnected});
    }

  }

  setupWeb3(){
  if (window.ethereum) {
    this.web3Provider = window.ethereum;
  }
  // Legacy dapp browsers...
  // else if (window.web3) {
  //   this.web3Provider = window.web3.currentProvider;
  // }
  // If no injected web3 instance is detected, fall back to Ganache
  else {
    this.web3Provider = new Web3.providers.HttpProvider('http://localhost:8545');
  }
  this.web3 = new Web3(this.web3Provider);
  this.contractInstance = new this.web3.eth.Contract(contractAbi, this.contractAddress);
  // this.web3.eth.getAccounts().then((accounts) => {
  //     [this.account] = accounts;
  //   });
  }

  disconnectMetamask(){
    // ({method: 'disconnect'
    // }).then((res) => {
    //   this.setState({isConnected: false});
    //   console.log(res);
    // }).catch((err) =>{
    //   console.log(err);
    // });
    this.setState({isConnected: !this.state.isConnected});
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          {/* <MenuBar 
          connectMetamask={this.connectMetamask} 
          disconnectMetamask={this.disconnectMetamask} 
          isConnected={this.state.isConnected} /> */}
        </header>
      </div>
    );
  }
}

export default App;
