import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import contractAbi from './abi'; //nft
import React, { Component } from 'react';
import MenuBar from './Components/MenuBar';

class App extends Component {

  constructor(props) {
    super(props);
    this.account = null;
    this.state = {
      isConnected: true
    };
    this.connectMetamask = this.connectMetamask.bind(this);
    this.disconnectMetamask = this.disconnectMetamask.bind(this);

  //this.contractAddress = '0x743B0f7FccE1c41661A0118F2cF37Cd500FcdC05';

  }

  connectMetamask() {
    if (window.ethereum) {
      window.ethereum.request({ method: 'eth_requestAccounts' 
      }).then((res) => {
        this.setState({isConnected: !this.state.isConnected});
        console.log("connectedToMetamask");
        this.account = res;
        console.log(this.account);
      }).catch((err) => {
        console.log(err)
        console.log("user denied connectedToMetamask")
      });    
    } else {
      // TODO alert
    }
  }

  setupWeb3(){
  if (window.ethereum) {
    this.web3Provider = window.ethereum;
    try {
      // Request account access
      //window.ethereum.enable();
      window.ethereum.request({ method: 'eth_accounts' }).then((res) => {});
    } catch (error) {
      // User denied account access...
      console.error("User denied account access")
    }
  }
  // Legacy dapp browsers...
  else if (window.web3) {
    this.web3Provider = window.web3.currentProvider;
  }
  // If no injected web3 instance is detected, fall back to Ganache
  else {
    this.web3Provider = new Web3.providers.HttpProvider('http://localhost:8545');
  }
  this.web3 = new Web3(this.web3Provider);
  this.contractInstance = new this.web3.eth.Contract(contractAbi, this.contractAddress);
  this.web3.eth.getAccounts().then((accounts) => {
      [this.account] = accounts;
    });
  }

  disconnectMetamask(){
    // ({method: 'disconnect'
    // }).then((res) => {
    //   this.setState({isConnected: false});
    //   console.log(res);
    // }).catch((err) =>{
    //   console.log(err);
    // });
    window.ethereum.resetApp();
    this.setState({isConnected: !this.state.isConnected});
    console.log(window.ethereum);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <MenuBar 
          connectMetamask={this.connectMetamask} 
          disconnectMetamask={this.disconnectMetamask} 
          isConnected={this.state.isConnected} />
        </header>
      </div>
    );
  }
}

export default App;
